import React, { useState } from "react";
import axios from "axios";
import Popup from "./Popup"; // Import your reusable Popup component

export default function Game() {
  const [currentLevel, setCurrentLevel] = useState(1); // Tracks current level
  const [loading, setLoading] = useState(false);
  const [apiResponse, setApiResponse] = useState(null);
  const [userAnswer, setUserAnswer] = useState("");
  const [incorrectAnswer, setIncorrectAnswer] = useState(false); // Tracks incorrect answers
  const [gameOver, setGameOver] = useState(false); // Tracks game completion

  // Fetch API data for the level
  const generateApiData = async () => {
    setLoading(true);
    setApiResponse(null);

    try {
      const response = await axios.get(
        `https://marcconrad.com/uob/banana/api.php?out=json`
      );

      if (response.status !== 200) {
        throw new Error(`API error: ${response.status} - ${response.statusText}`);
      }

      setApiResponse({
        image: response.data.question,
        solution: response.data.solution,
      });
    } catch (error) {
      console.error("Error fetching API data:", error);
      alert("Failed to fetch data. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  // Check user's answer
  const checkAnswer = () => {
    if (userAnswer.trim() === String(apiResponse.solution)) {
      if (currentLevel === 3) {
        setGameOver(true); // End game at level 3
      } else {
        setCurrentLevel((prev) => prev + 1); // Go to the next level
        setUserAnswer(""); // Clear input for the next level
        setApiResponse(null); // Clear the previous question
        generateApiData(); // Fetch new question for the next level
      }
    } else {
      setIncorrectAnswer(true); // Show incorrect answer popup
    }
  };

  // Reset the game
  const resetGame = () => {
    setCurrentLevel(1);
    setGameOver(false);
    setUserAnswer("");
    setApiResponse(null);
  };

  return (
    <div className="game-container">
      {/* Game Over Popup */}
      {gameOver && (
        <Popup
          message="🎉 Congratulations! You've completed the game! 🎉"
          onClose={resetGame}
        />
      )}

      {/* Incorrect Answer Popup */}
      {incorrectAnswer && (
        <Popup
          message="Incorrect answer! Try again."
          onClose={() => setIncorrectAnswer(false)}
        />
      )}

      {/* Game Content */}
      {!gameOver && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-40">
          <div className="bg-black p-6 rounded-lg shadow-lg text-center w-full max-w-4xl relative z-30">
            <h2 className="text-5xl font-extrabold text-amber-400 mb-4">
              🤷‍♂️ Bananuiz: Level {currentLevel} 🤷‍♂️
            </h2>

            {!apiResponse ? (
              <button
                className="bg-blue-500 text-white px-4 py-2 rounded"
                onClick={generateApiData}
                disabled={loading}
              >
                {loading ? "Fetching..." : "Fetch Question"}
              </button>
            ) : (
              <>
                <img
                  src={apiResponse.image}
                  alt="Question"
                  className="my-4 mx-auto"
                />
                <input
                  className="border border-gray-300 p-2 rounded w-full max-w-md"
                  placeholder="Enter your answer"
                  value={userAnswer}
                  onChange={(e) => setUserAnswer(e.target.value)}
                />

                <div className="flex justify-center gap-3 mt-4">
                  {[...Array(10).keys()].map((num) => (
                    <button
                      key={num}
                      className="bg-neutral-400 hover:bg-yellow-300 rounded-full w-12 h-12 text-lg font-bold flex items-center justify-center"
                      onClick={() => setUserAnswer((prev) => prev + num)}
                    >
                      {num}
                    </button>
                  ))}
                </div>

                <div className="flex justify-between mt-4">
                  <button
                    className="bg-blue-500 text-white px-4 py-2 rounded ml-2"
                    onClick={() => setUserAnswer("")}
                  >
                    Clear
                  </button>

                  <button
                    className="bg-green-500 text-white px-4 py-2 rounded mr-2"
                    onClick={checkAnswer}
                  >
                    Submit
                  </button>
                </div>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
}
