import React, { useState, useEffect } from "react";

export default function Level({ level, question, onLevelComplete }) {
  const [userAnswer, setUserAnswer] = useState("");
  const [hearts, setHearts] = useState(4); // User's lives
  const [timer, setTimer] = useState(20); // Timer duration
  const [quizEnded, setQuizEnded] = useState(false);
  const [popupMessage, setPopupMessage] = useState("");
  const [showPopup, setShowPopup] = useState(false);

  useEffect(() => {
    // Countdown Timer
    if (timer > 0 && !quizEnded) {
      const intervalId = setInterval(() => setTimer((prev) => prev - 1), 1000);
      return () => clearInterval(intervalId); // Clear interval on unmount
    } else if (timer === 0 && !quizEnded) {
      // Time's up
      endGame("Time's up! Game over.");
    }
  }, [timer, quizEnded]);

  const handleAnswer = (selectedOption) => {
    if (quizEnded) return; // Stop handling if quiz has ended

    if (selectedOption === question.answer) {
      // Correct answer
      onLevelComplete && onLevelComplete(); // Notify parent to handle level completion
    } else {
      // Incorrect answer
      if (hearts > 1) {
        setHearts(hearts - 1); // Deduct a heart
        showPopupMessage("Incorrect answer! Try again.");
      } else {
        // No hearts left
        endGame("No hearts left! Game over.");
      }
    }
  };

  const endGame = (message) => {
    setQuizEnded(true);
    setPopupMessage(message);
    setShowPopup(true);
  };

  const showPopupMessage = (message) => {
    setPopupMessage(message);
    setShowPopup(true);
  };

  const handlePopupClose = () => {
    setShowPopup(false);
  };

  const resetLevel = () => {
    setUserAnswer("");
    setTimer(20);
    setHearts(4);
    setQuizEnded(false);
    setPopupMessage("");
    setShowPopup(false);
  };

  return (
    <div className="text-center">
      <h2 className="text-3xl font-bold">Level {level}</h2>
      <div className="flex justify-between items-center mt-4">
        <div className="text-lg font-semibold">⏰ Time Left: {timer}s</div>
        <div>
          {Array.from({ length: hearts }, (_, index) => (
            <span key={index} className="text-red-500 text-2xl mx-1">❤️</span>
          ))}
        </div>
      </div>

      <p className="mt-6 text-lg">{question.text}</p>
      <div className="grid grid-cols-2 gap-4 mt-4">
        {question.options.map((option, index) => (
          <button
            key={index}
            onClick={() => handleAnswer(option)}
            disabled={quizEnded}
            className={`px-4 py-2 rounded-lg font-semibold ${
              quizEnded
                ? "bg-gray-400 cursor-not-allowed"
                : "bg-blue-500 hover:bg-blue-700 text-white"
            }`}
          >
            {option}
          </button>
        ))}
      </div>

      {showPopup && (
        <div className="mt-6 p-4 bg-gray-800 text-white rounded-md">
          <p>{popupMessage}</p>
          <button
            onClick={handlePopupClose}
            className="mt-2 px-4 py-2 bg-blue-500 hover:bg-blue-700 rounded"
          >
            Close
          </button>
        </div>
      )}

      {quizEnded && (
        <button
          onClick={resetLevel}
          className="mt-6 px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-700"
        >
          Restart Level
        </button>
      )}
    </div>
  );
}
