import React, { useState, useEffect } from 'react';
import backgroundImage from "../../../assets/th (1).jpeg";

export default function Level1({ nextLevel, updateScore, score }) {
  const [question, setQuestion] = useState({
    text: "What is the capital of France?",
    options: ["Paris", "London", "Berlin", "Madrid"],
    answer: "Paris"
  });

  const [timer, setTimer] = useState(20);
  const [hearts, setHearts] = useState(5);
  const [quizEnded, setQuizEnded] = useState(false);

  useEffect(() => {
    if (timer === 0) {
      setQuizEnded(true);
      alert("Time's up! Game over.");
      return;
    }

    const interval = setInterval(() => {
      if (timer > 0) {
        setTimer(prev => prev - 1);
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [timer]);

  const handleAnswer = (option) => {
    if (quizEnded) return;

    if (option === question.answer) {
      updateScore(10);
      nextLevel();
    } else {
      if (hearts > 1) {
        setHearts(hearts - 1);
        alert("Incorrect answer! Try again.");
      } else {
        setHearts(0);
        setQuizEnded(true);
        alert("No hearts left! Game over.");
      }
    }
  };

  const formatTime = (timeInSeconds) => {
    return timeInSeconds < 10 ? `0${timeInSeconds}` : `${timeInSeconds}`;
  };

  const handleLogOut = () => {
    alert("Logging out...");
  };

  const handleNewGame = () => {
    alert("Starting a new game...");
    setTimer(20);
    setHearts(5);
    setQuizEnded(false);
    updateScore(0);
    nextLevel();
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-cover bg-center" style={{ backgroundImage: `url(${backgroundImage})` }}>
      {/* Header */}
      <header className="absolute top-0 left-0 w-full bg-blue-900 py-4">
        <h1 className="text-center text-white text-3xl font-bold">Bananauize</h1>
      </header>

      <div className="w-full max-w-lg p-8 space-y-6 rounded-lg shadow-lg backdrop-blur-sm" style={{ backgroundColor: 'rgba(255, 255, 255, 0.2)' }}>
        <h2 className="text-2xl font-semibold text-center text-blue-700">Level 1: Warm-Up</h2>
        
        <div className="flex justify-between items-center">
          <p className="text-lg text-gray-800">{formatTime(timer)} seconds</p>
          <div className="flex items-center">
            {Array.from({ length: hearts }, (_, index) => (
              <span key={index} className="text-yellow-500 text-2xl">üçå</span>
            ))}
          </div>
        </div>

        <p className="text-lg text-center text-gray-800">{question.text}</p>
        
        <div className="grid grid-cols-2 gap-4">
          {question.options.map((option) => (
            <button
              key={option}
              onClick={() => handleAnswer(option)}
              className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-700 transition duration-200"
            >
              {option}
            </button>
          ))}
        </div>

        <div className="mt-6 flex justify-between">
          <button
            onClick={handleLogOut}
            className="px-4 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-700 transition duration-200"
          >
            LogOut
          </button>

          <button
            onClick={handleNewGame}
            className="px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-700 transition duration-200"
          >
            New Game
          </button>
        </div>
      </div>
    </div>
  );
}
