import React, { useState } from 'react';

export default function ApiPopup({ onClose }) {
  const [loading, setLoading] = useState(false);
  const [apiResponse, setApiResponse] = useState(null);

  const generateApiData = async () => {
    setLoading(true);
    try {
      // Replace with your actual API endpoint
      const response = await fetch(" http://marcconrad.com/uob/banana/api.php");
      const data = await response.json();
      setApiResponse(data);
    } catch (error) {
      setApiResponse({ error: "Failed to fetch data. Please try again." });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
      <div className="bg-white p-6 rounded-lg shadow-lg text-center w-96">
        <h2 className="text-2xl font-bold mb-4">Incorrect Answer</h2>
        <p className="mb-4">Fetching a hint or additional data...</p>
        <button
          onClick={generateApiData}
          className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-700 transition duration-200 font-semibold"
        >
          {loading ? "Loading..." : "Generate Data"}
        </button>

        {apiResponse && (
          <div className="mt-4 text-left">
            <p className="text-sm text-gray-600">{JSON.stringify(apiResponse)}</p>
          </div>
        )}

        <button
          onClick={onClose}
          className="mt-4 px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-700 transition duration-200 font-semibold"
        >
          Close
        </button>
      </div>
    </div>
  );
}
