import React, { useState } from "react";
import axios from "axios";

export default function ApiPopup({ onClose, isCorrect }) {
  const [loading, setLoading] = useState(false);
  const [apiResponse, setApiResponse] = useState(null);
  const [userAnswer, setUserAnswer] = useState("");
  const [errorMessage, setErrorMessage] = useState(null);

  // Function to validate user's answer and fetch data from the API
  const validateAndFetch = async () => {
    if (userAnswer.trim() === "correct_answer") { // Replace with your specific correct answer logic
      setLoading(true);
      setErrorMessage(null);

      try {
        const response = await axios.get(
          `https://marcconrad.com/uob/banana/api.php?out=json`
        );

        if (response.status !== 200) {
          throw new Error(
            `API error: ${response.status} - ${response.statusText}`
          );
        }

        console.log("API Response:", response.data);

        setApiResponse({
          image: response.data.question, // Assuming 'question' contains the image URL
          solution: response.data.solution,
        });
      } catch (error) {
        console.error("Error fetching API data:", error);
        setErrorMessage("Failed to fetch data. Please try again.");
      } finally {
        setLoading(false);
      }
    } else {
      setErrorMessage("Incorrect answer. API will not integrate.");
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
      <div className="bg-white p-6 rounded-lg shadow-lg text-center w-full max-w-4xl">
        <h2 className="text-2xl font-bold mb-4">
          {isCorrect ? "Correct Answer" : "Incorrect Answer"}
        </h2>
        <p className="mb-4">
          {isCorrect
            ? "Fetching a reward or additional data..."
            : "Fetching a hint or additional data..."}
        </p>

        {/* Input for user's answer */}
        <div className="mb-4">
          <input
            type="text"
            placeholder="Enter your answer"
            value={userAnswer}
            onChange={(e) => setUserAnswer(e.target.value)}
            className="border border-gray-300 p-2 rounded w-full max-w-sm mx-auto"
          />
        </div>

        {/* Button to validate and fetch data */}
        <button
          onClick={validateAndFetch}
          className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-700 transition duration-200 font-semibold"
        >
          {loading ? "Loading..." : "Submit Answer"}
        </button>

        {/* Display API data */}
        {apiResponse && (
          <div className="mt-4 text-left">
            {/* Show the question image */}
            {apiResponse.image ? (
              <div className="mb-4">
                <img
                  src={apiResponse.image}
                  alt="Question Image"
                  className="w-full max-w-md mx-auto rounded"
                />
              </div>
            ) : (
              <p className="text-gray-600">
                No image available for this question.
              </p>
            )}
          </div>
        )}

        {/* Display error message */}
        {errorMessage && (
          <div className="mt-4 text-red-600">{errorMessage}</div>
        )}

        {/* Close button */}
        <button
          onClick={onClose}
          className="mt-4 px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-700 transition duration-200 font-semibold"
        >
          Close
        </button>
      </div>
    </div>
  );
}
