import React, { useState } from 'react';
import TextInput from "../../components/ui/TextInput";
import Button from "./common/Button";
import Form from "./common/Form";

export default function Login({ backgroundImage }) {
  const [formData, setFormData] = useState({ email: '', password: '' });

  const handleChange = (e) => {
    const { id, value } = e.target;
    setFormData({ ...formData, [id]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Submit logic here (API call, validation, etc.)
    console.log("Form submitted:", formData);
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-cover bg-center" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <div className="w-full max-w-md p-8 space-y-6 bg-white bg-opacity-70 rounded-lg shadow-lg backdrop-blur-sm">
        <h2 className="text-2xl font-bold text-center text-gray-800">Login to Your Account</h2>
        
        <Form onSubmit={handleSubmit}>
          <TextInput
            label="Email"
            type="email"
            id="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="Enter your email"
          />
          <TextInput
            label="Password"
            type="password"
            id="password"
            value={formData.password}
            onChange={handleChange}
            placeholder="Enter your password"
          />
          <Button type="submit">Login</Button>
        </Form>

        <div className="text-center">
          <p className="text-sm text-gray-600">
            Donâ€™t have an account?{" "}
            <a href="/register" className="font-medium text-indigo-600 hover:text-indigo-500">
              Sign up
            </a>
          </p>
        </div>
      </div>
    </div>
  );
}
