import React, { useState, useEffect } from 'react';
import backgroundImage from "../../../assets/th (1).jpeg";
import Popup from './Popup';

export default function Level1({ nextLevel, updateScore, score }) {
  const [question, setQuestion] = useState({
    text: "What is the capital of France?",
    options: ["Paris", "London", "Berlin", "Madrid"],
    answer: "Paris"
  });

  const [timer, setTimer] = useState(20);
  const [hearts, setHearts] = useState(5);
  const [quizEnded, setQuizEnded] = useState(false);
  const [popupMessage, setPopupMessage] = useState('');
  const [showPopup, setShowPopup] = useState(false);

  useEffect(() => {
    if (timer === 0) {
      setQuizEnded(true);
      setPopupMessage("Time's up! Game over.");
      setShowPopup(true);
      return;
    }

    const interval = setInterval(() => {
      if (timer > 0) {
        setTimer(prev => prev - 1);
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [timer]);

  const handleAnswer = (option) => {
    if (quizEnded) return;

    if (option === question.answer) {
      updateScore(10);
      nextLevel();
    } else {
      if (hearts > 1) {
        setHearts(hearts - 1);
        setPopupMessage("Incorrect answer! Try again.");
        setShowPopup(true);
      } else {
        setHearts(0);
        setQuizEnded(true);
        setPopupMessage("No hearts left! Game over.");
        setShowPopup(true);
      }
    }
  };

  const handlePopupClose = () => {
    setShowPopup(false);
  };

  const handleLogOut = () => {
    setPopupMessage("Logging out...");
    setShowPopup(true);
  };

  const handleNewGame = () => {
    setTimer(20);
    setHearts(5);
    setQuizEnded(false);
    updateScore(0);
    nextLevel();
  };

  return (
    <div 
      className="flex flex-col items-center justify-center min-h-screen bg-cover bg-center" 
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      <header className="fixed top-4 text-center text-5xl font-extrabold text-yellow-300">
        🤷‍♂️  Bananuiz  🤷‍♂️
      </header>

      <div className="w-full max-w-lg p-8 space-y-6 rounded-lg shadow-lg backdrop-blur-md bg-white/10 mt-24">
        <h2 className="text-3xl font-bold text-center text-white">Level 1</h2>
        
        <div className="flex justify-between items-center">
          <div className="flex items-center text-lg font-semibold text-white">
            <span className="mr-2">⏰</span>
            <span>{timer < 10 ? `0${timer}` : timer} sec</span>
          </div>
          <div className="flex items-center space-x-1">
            {Array.from({ length: hearts }, (_, index) => (
              <span key={index} className="text-yellow-500 text-2xl">🍌</span>
            ))}
          </div>
        </div>

        <p className="text-xl font-semibold text-center text-white mt-4">{question.text}</p>
        
        <div className="grid grid-cols-2 gap-4 mt-4">
          {question.options.map((option) => (
            <button
              key={option}
              onClick={() => handleAnswer(option)}
              className="px-4 py-2 bg-yellow-400 text-white rounded-lg hover:bg-yellow-500 transition duration-200 font-bold"
            >
              {option}
            </button>
          ))}
        </div>

        <div className="mt-6 flex justify-between">
          <button
            onClick={handleLogOut}
            className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-800 transition duration-200 font-semibold"
          >
            Log Out
          </button>

          <button
            onClick={handleNewGame}
            className="px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-700 transition duration-200 font-semibold"
          >
            New Game
          </button>
        </div>
      </div>

      {showPopup && (
        <Popup
          message={popupMessage}
          onClose={handlePopupClose}
        />
      )}
    </div>
  );
}
