import React, { useState } from "react";
import ApiPopup from "../../components/ui/ApiPopup";

export default function Game() {
  const [level, setLevel] = useState(1);
  const [showPopup, setShowPopup] = useState(false);

  const nextLevel = () => {
    if (level < 3) {
      setLevel(level + 1);
    } else {
      alert("ðŸŽ‰ Game Over! You completed all levels! ðŸŽ‰");
    }
  };

  const handlePopupClose = (success) => {
    setShowPopup(false);
    if (success) {
      nextLevel();
    }
  };

  return (
    <div className="game-container text-center">
      <h1 className="text-3xl font-bold my-6">Welcome to Bananuiz</h1>
      <h2 className="text-2xl mb-4">Level {level}</h2>

      <button
        onClick={() => setShowPopup(true)}
        className="bg-blue-500 text-white px-6 py-3 rounded hover:bg-blue-700"
      >
        Start Level {level}
      </button>

      {showPopup && (
        <ApiPopup
          key={level} // Ensures a fresh instance for each level
          onClose={handlePopupClose}
          nextLevel={nextLevel}
        />
      )}
    </div>
  );
}
