import { useState } from "react";
import axios from "axios";

export default function ApiPopup({ onClose, nextLevel }) {
  const [loading, setLoading] = useState(false);
  const [apiResponse, setApiResponse] = useState(null);
  const [userAnswer, setUserAnswer] = useState("");

  const generateApiData = async () => {
    setLoading(true);
    setApiResponse(null);

    try {
      const response = await axios.get(
        `https://marcconrad.com/uob/banana/api.php?out=json`
      );

      if (response.status !== 200) {
        throw new Error(`API error: ${response.status} - ${response.statusText}`);
      }

      setApiResponse({
        image: response.data.question,
        solution: response.data.solution,
      });
    } catch (error) {
      console.error("Error fetching API data:", error);
      alert("Failed to fetch data. Please try again.");
      onClose(false); // Notify parent of failure
    } finally {
      setLoading(false);
    }
  };

  const handleNumberClick = (number) => {
    setUserAnswer((prev) => prev + number);
  };

  const handleClear = () => {
    setUserAnswer("");
  };

  const checkAnswer = () => {
    if (userAnswer.trim() === String(apiResponse.solution)) {
      alert("Correct Answer!");
      onClose(true); // Notify parent of success
      nextLevel && nextLevel(); // Proceed to the next level
    } else {
      alert("Incorrect answer.");
      onClose(false); // Notify parent of failure
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
      <div className="bg-white p-6 rounded-lg shadow-lg text-center w-full max-w-4xl">
        <h2 className="text-2xl font-bold mb-6">API Question</h2>

        {!apiResponse ? (
          <button
            className="bg-blue-500 text-white px-4 py-2 rounded"
            onClick={generateApiData}
            disabled={loading}
          >
            {loading ? "Fetching..." : "Fetch Question"}
          </button>
        ) : (
          <>
            <img
              src={apiResponse.image}
              alt="Question"
              className="my-4 mx-auto"
            />
            <div className="mb-4 text-lg">Your Answer: {userAnswer}</div>
            <div className="grid grid-cols-3 gap-2 mb-6 max-w-sm mx-auto">
              {[...Array(10).keys()].map((num) => (
                <button
                  key={num}
                  className="bg-gray-300 hover:bg-gray-400 text-black font-semibold py-2 px-4 rounded"
                  onClick={() => handleNumberClick(num)}
                >
                  {num}
                </button>
              ))}
              <button
                className="col-span-3 bg-red-500 hover:bg-red-600 text-white font-semibold py-2 px-4 rounded"
                onClick={handleClear}
              >
                Clear
              </button>
            </div>
            <button
              className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded"
              onClick={checkAnswer}
            >
              Submit
            </button>
          </>
        )}
        <button
          className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 mt-4 rounded"
          onClick={() => onClose(false)}
        >
          Close
        </button>
      </div>
    </div>
  );
}
