import { useState, useEffect } from "react";
import axios from "axios";

export default function ApiPopup({ onClose, nextLevel }) {
  const [loading, setLoading] = useState(false);
  const [apiResponse, setApiResponse] = useState(null);
  const [userAnswer, setUserAnswer] = useState("");
  const [timer, setTimer] = useState(20);

  useEffect(() => {
    if (timer === 0) {
      alert("Time's up!");
      onClose(false); // Notify parent of failure due to timeout
      return;
    }

    const interval = setInterval(() => {
      setTimer((prev) => prev - 1);
    }, 1000);

    return () => clearInterval(interval);
  }, [timer]);

  const generateApiData = async () => {
    setLoading(true);
    setApiResponse(null);

    try {
      const response = await axios.get(
        `https://marcconrad.com/uob/banana/api.php?out=json`
      );

      if (response.status !== 200) {
        throw new Error(`API error: ${response.status} - ${response.statusText}`);
      }

      setApiResponse({
        image: response.data.question,
        solution: response.data.solution,
      });
    } catch (error) {
      console.error("Error fetching API data:", error);
      alert("Failed to fetch data. Please try again.");
      onClose(false); // Notify parent of failure
    } finally {
      setLoading(false);
    }
  };

  const handleNumberClick = (number) => {
    setUserAnswer((prev) => prev + number);
  };

  const checkAnswer = () => {
    if (userAnswer.trim() === String(apiResponse.solution)) {
      alert("Correct Answer!");
      onClose(true); // Notify parent of success
      nextLevel && nextLevel(); // Proceed to the next level
    } else {
      alert("Incorrect answer.");
      onClose(false); // Notify parent of failure
    }
  };

  const clearInput = () => setUserAnswer("");

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
      <div className="bg-white p-6 rounded-lg shadow-lg text-center w-full max-w-4xl relative">
        {/* Close Button Positioned in the Top Right Corner */}
        <button
          onClick={onClose}
          className="absolute top-4 right-4 bg-slate-700 text-white px-4 py-2 rounded hover:bg-slate-400 transition"
        >
          ‚ùå
        </button>

        {/* Header with Timer and Title */}
        <div className="flex justify-between items-center mb-4">
          <div className="text-lg font-bold text-red-600 flex items-center">
            ‚è∞ <span className="ml-2">{timer} sec</span>
          </div>
          <h2 className="text-5xl font-extrabold text-amber-400">
            ü§∑‚Äç‚ôÇÔ∏è Bananuiz ü§∑‚Äç‚ôÇÔ∏è
          </h2>
        </div>

        {!apiResponse ? (
          <button
            className="bg-blue-500 text-white px-4 py-2 rounded"
            onClick={generateApiData}
            disabled={loading}
          >
            {loading ? "Fetching..." : "Fetch Question"}
          </button>
        ) : (
          <>
            <img
              src={apiResponse.image}
              alt="Question"
              className="my-4 mx-auto"
            />
            <input
              className="border border-gray-300 p-2 rounded w-full max-w-md"
              placeholder="Enter your answer"
              value={userAnswer}
              onChange={(e) => setUserAnswer(e.target.value)}
            />

            <div className="flex justify-center gap-3 mt-4">
              {[...Array(10).keys()].map((num) => (
                <button
                  key={num}
                  className="bg-neutral-400 hover:bg-yellow-300 rounded-full w-12 h-12 text-lg font-bold flex items-center justify-center"
                  onClick={() => handleNumberClick(num)}
                >
                  {num}
                </button>
              ))}
            </div>

            <button
              className="bg-green-500 text-white px-4 py-2 mt-4 rounded"
              onClick={checkAnswer}
            >
              Submit
            </button>

            <button
              className="bg-yellow-500 text-white px-4 py-2 mt-4 rounded ml-2"
              onClick={clearInput}
            >
              Clear
            </button>
          </>
        )}
      </div>
    </div>
  );
}
