import React, { useState } from "react";
import axios from "axios";

export default function ApiPopup({ onClose, nextLevel }) {
  const [loading, setLoading] = useState(false);
  const [apiResponse, setApiResponse] = useState(null);
  const [userAnswer, setUserAnswer] = useState("");

  const fetchApiQuestion = async () => {
    setLoading(true);
    try {
      const response = await axios.get(
        `https://marcconrad.com/uob/banana/api.php?out=json`
      );
      setApiResponse({
        image: response.data.question,
        solution: String(response.data.solution),
      });
    } catch (error) {
      console.error("Error fetching API question:", error);
      alert("Failed to fetch the question. Please try again.");
      onClose(false);
    } finally {
      setLoading(false);
    }
  };

  const checkAnswer = () => {
    if (userAnswer.trim() === apiResponse.solution) {
      alert("Correct Answer!");
      onClose(true); // Notify success
      nextLevel && nextLevel();
    } else {
      alert("Incorrect answer. Try again!");
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
      <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-md">
        <button
          className="absolute top-4 right-4 bg-red-500 text-white px-4 py-2 rounded"
          onClick={() => onClose(false)}
        >
          Close
        </button>

        <h2 className="text-2xl font-bold mb-4">API Challenge</h2>

        {!apiResponse ? (
          <button
            className="bg-blue-500 text-white px-4 py-2 rounded"
            onClick={fetchApiQuestion}
            disabled={loading}
          >
            {loading ? "Loading..." : "Fetch Question"}
          </button>
        ) : (
          <>
            <img
              src={apiResponse.image}
              alt="API Question"
              className="w-full h-auto my-4"
            />
            <input
              type="text"
              className="border p-2 rounded w-full mb-4"
              value={userAnswer}
              onChange={(e) => setUserAnswer(e.target.value)}
              placeholder="Enter your answer"
            />
            <button
              className="bg-green-500 text-white px-4 py-2 rounded"
              onClick={checkAnswer}
            >
              Submit Answer
            </button>
          </>
        )}
      </div>
    </div>
  );
}
