import React, { useState, useEffect } from 'react';
import backgroundImage from "../../../assets/th (1).jpeg";

export default function Level1({ nextLevel, updateScore, score }) {
  const [question, setQuestion] = useState({
    text: "What is the capital of France?",
    options: ["Paris", "London", "Berlin", "Madrid"],
    answer: "Paris"
  });

  const [timer, setTimer] = useState(20); // Timer set to 20 seconds
  const [hearts, setHearts] = useState(5); // 5 hearts available
  const [quizEnded, setQuizEnded] = useState(false);

  // Timer countdown effect
  useEffect(() => {
    if (timer === 0) {
      setQuizEnded(true);
      alert("Time's up! Game over.");
      return;
    }

    const interval = setInterval(() => {
      if (timer > 0) {
        setTimer(prev => prev - 1);
      }
    }, 1000);

    return () => clearInterval(interval); // Cleanup interval on unmount
  }, [timer]);

  const handleAnswer = (option) => {
    if (quizEnded) return; // Prevent answering after time is up

    if (option === question.answer) {
      updateScore(10);
      nextLevel(); // Go to next level if the answer is correct
    } else {
      if (hearts > 1) {
        setHearts(hearts - 1); // Decrease heart on wrong answer
        alert("Incorrect answer! Try again.");
      } else {
        setHearts(0); // No hearts left, game over
        setQuizEnded(true);
        alert("No hearts left! Game over.");
      }
    }
  };

  const formatTime = (timeInSeconds) => {
    return timeInSeconds < 10 ? `0${timeInSeconds}` : `${timeInSeconds}`;
  };

  const handleLogOut = () => {
    alert("Logging out..."); // Implement logout logic
    // Redirect to login page or reset game state
  };

  const handleNewGame = () => {
    setTimer(20);
    setHearts(5); // Reset hearts
    setQuizEnded(false);
    updateScore(0); // Reset score
    nextLevel(); // Reset to the first level
  };

  return (
    <div 
      className="flex flex-col items-center justify-center min-h-screen bg-cover bg-center" 
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      <header className="fixed top-4 text-center text-5xl font-extrabold text-yellow-300">
        Bananauize
      </header>

      <div className="w-full max-w-lg p-8 space-y-6 rounded-lg shadow-lg backdrop-blur-md bg-white/10 mt-24">
        <h2 className="text-3xl font-bold text-center text-white">Level 1</h2>
        
        <div className="flex justify-between items-center">
          {/* Timer with clock icon */}
          <div className="flex items-center text-lg font-semibold text-white">
            <span className="mr-2">‚è∞</span> {/* Clock icon */}
            <span>{formatTime(timer)} sec</span>
          </div>
          <div className="flex items-center space-x-1">
            {/* Display bananas as lives */}
            {Array.from({ length: hearts }, (_, index) => (
              <span key={index} className="text-yellow-500 text-2xl">üçå</span>
            ))}
          </div>
        </div>

        <p className="text-xl font-semibold text-center text-white mt-4">{question.text}</p>
        
        <div className="grid grid-cols-2 gap-4 mt-4">
          {question.options.map((option) => (
            <button
              key={option}
              onClick={() => handleAnswer(option)}
              className="px-4 py-2 bg-yellow-400 text-black rounded-lg hover:bg-yellow-500 transition duration-200 font-semibold shadow-md"
            >
              {option}
            </button>
          ))}
        </div>

        <div className="mt-6 flex justify-between">
          <button
            onClick={handleLogOut}
            className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-800 transition duration-200 font-semibold"
          >
            Log Out
          </button>

          <button
            onClick={handleNewGame}
            className="px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-700 transition duration-200 font-semibold"
          >
            New Game
          </button>
        </div>
      </div>
    </div>
  );
}
