import React, { useState } from "react";
import axios from "axios";
import Popup from "./Popup"; // Reusable popup for incorrect answers

export default function ApiPopup({ onClose }) {
  const [loading, setLoading] = useState(false);
  const [apiResponse, setApiResponse] = useState(null);
  const [userAnswer, setUserAnswer] = useState("");
  const [incorrectAnswer, setIncorrectAnswer] = useState(false);

  const fetchApiData = async () => {
    setLoading(true);
    setApiResponse(null);

    try {
      const response = await axios.get(
        `https://marcconrad.com/uob/banana/api.php?out=json`
      );

      if (response.status !== 200) {
        throw new Error(`API error: ${response.status} - ${response.statusText}`);
      }

      setApiResponse({
        image: response.data.question,
        solution: response.data.solution,
      });
    } catch (error) {
      console.error("Error fetching API data:", error);
      alert("Failed to fetch data. Please try again.");
      onClose(false);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = () => {
    if (userAnswer.trim() === String(apiResponse.solution)) {
      alert("Correct Answer!");
      onClose(true);
    } else {
      setIncorrectAnswer(true);
    }
  };

  const clearAnswer = () => setUserAnswer("");

  const closeIncorrectPopup = () => setIncorrectAnswer(false);

  return (
    <div>
      {incorrectAnswer && (
        <Popup message="Incorrect answer! Try again." onClose={closeIncorrectPopup} />
      )}

      <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
        <div className="bg-white p-6 rounded-lg shadow-lg text-center w-full max-w-4xl">
          <button
            onClick={() => onClose(false)}
            className="absolute top-4 right-4 bg-gray-700 text-white px-4 py-2 rounded"
          >
            ‚ùå
          </button>

          <h2 className="text-3xl font-bold mb-4">Bananuiz Challenge</h2>

          {!apiResponse ? (
            <button
              onClick={fetchApiData}
              className="bg-blue-500 text-white px-4 py-2 rounded"
              disabled={loading}
            >
              {loading ? "Loading..." : "Fetch Question"}
            </button>
          ) : (
            <>
              <img
                src={apiResponse.image}
                alt="Question"
                className="my-4 mx-auto"
              />
              <input
                className="border p-2 rounded w-full max-w-md"
                placeholder="Enter your answer"
                value={userAnswer}
                onChange={(e) => setUserAnswer(e.target.value)}
              />

              <div className="flex justify-between mt-4">
                <button
                  onClick={clearAnswer}
                  className="bg-red-500 text-white px-4 py-2 rounded"
                >
                  Clear
                </button>

                <button
                  onClick={handleSubmit}
                  className="bg-green-500 text-white px-4 py-2 rounded"
                >
                  Submit
                </button>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
}
